//SPDX-License-Identifier: MIT

pragma solidity ^0.8.20;

import {LinkTokenInterface} from "@chainlink/contracts/src/v0.8/shared/interfaces/LinkTokenInterface.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "./TogetherForCharityWithTime.sol";
import "./TogetherForCharityWithTarget.sol";

struct RegistrationParams {
    string name;
    bytes encryptedEmail;
    address upkeepContract;
    uint32 gasLimit;
    address adminAddress;
    uint8 triggerType;
    bytes checkData;
    bytes triggerConfig;
    bytes offchainConfig;
    uint96 amount;
}

interface AutomationRegistrarInterface {
    function registerUpkeep(
        RegistrationParams calldata requestParams
    ) external returns (uint256);
}

error TogetherForCharityContractFactory__RegisterUpkeepFailed();
error TogetherForCharityContractFactory__TransferFailed();

contract TogetherForCharityContractFactory {
    /* Events */
    event CampaignCreated(
        uint256 campaignID,
        address indexed campaignAddress,
        address indexed creator,
        address indexed beneficiary
    );

    event EthSentToCampaign(
        uint256 campaignID,
        address indexed campaignAddress,
        uint256 amount
    );

    LinkTokenInterface public immutable i_link;
    AutomationRegistrarInterface public immutable i_registrar;
    mapping(uint256 => address) public upkeepIDToCampaignAddress;
    uint32 gasLimit;

    address[] public deployedCampaigns;
    uint256 private numberOfCampaigns;

    constructor(
        LinkTokenInterface link,
        AutomationRegistrarInterface registrar
    ) {
        i_link = link;
        i_registrar = registrar;
        gasLimit = 300000;

        deployedCampaigns = new address[](0);
        numberOfCampaigns = 0;
    }

    function createCampaignWithTarget(
        string memory description,
        address beneficiary,
        uint256 targetAmount,
        uint256 minimumAmount
    ) public payable {
        numberOfCampaigns += 1;
        TogetherForCharityWithTarget newCampaign = new TogetherForCharityWithTarget(
                numberOfCampaigns,
                description,
                msg.sender,
                beneficiary,
                msg.value,
                targetAmount,
                minimumAmount
            );

        deployedCampaigns.push(address(newCampaign));

        emit CampaignCreated(
            numberOfCampaigns,
            address(newCampaign),
            msg.sender,
            beneficiary
        );
    }

    function createCampaignWithTime(
        string memory description,
        address beneficiary,
        uint256 totalTime,
        uint256 minimumAmount
    ) public payable {
        numberOfCampaigns += 1;
        TogetherForCharityWithTime newCampaign = new TogetherForCharityWithTime(
            numberOfCampaigns,
            description,
            msg.sender,
            beneficiary,
            msg.value,
            totalTime,
            minimumAmount
        );

        RegistrationParams memory newRegistrationParams = RegistrationParams(
            string.concat("Campaign ", Strings.toString(numberOfCampaigns)),
            "0x",
            address(newCampaign),
            gasLimit,
            address(this),
            0,
            "0x",
            "0x",
            "0x",
            1000000000000000000
        );

        registerAndPredictID(newRegistrationParams);

        deployedCampaigns.push(address(newCampaign));

        emit CampaignCreated(
            numberOfCampaigns,
            address(newCampaign),
            msg.sender,
            beneficiary
        );

        newCampaign.fundCampaign{value: msg.value}(msg.sender);

        emit EthSentToCampaign(
            numberOfCampaigns,
            address(newCampaign),
            msg.value
        );
    }

    function registerAndPredictID(RegistrationParams memory params) public {
        i_link.approve(address(i_registrar), params.amount);
        uint256 upkeepID = i_registrar.registerUpkeep(params);

        if (upkeepID != 0) {
            upkeepIDToCampaignAddress[upkeepID] = params.upkeepContract;
        } else {
            revert TogetherForCharityContractFactory__RegisterUpkeepFailed();
        }
    }

    function getDeployedCampaigns() public view returns (address[] memory) {
        return deployedCampaigns;
    }
}

